import React, { useState, useRef } from "react";
import ReactFlow, { ReactFlowProvider, addEdge, removeElements, Controls, Handle } from "react-flow-renderer";



import "./Centerflow.css";

const InputNode = ({ type, data }) => {
  return (
    <>
      <Handle type="target" position="left" />
      <div className="box-container">
            <ul>
              <li>Open Browser</li>
              <li>
                <span class="ico-edit">edit</span>
                <span class="ico-dele">Delete</span>
              </li>
            </ul>
      </div>
      <Handle type="source" position="right" id="a" />
    </>
  );

};

const nodeTypes = {
  InputNode: InputNode,
};

const initialElements = [
  {
    id: 'horizontal-1',
    sourcePosition: 'right',
    type: 'InputNode',
    className: 'dark-node',
    data: {},
    position: { x: 0, y: 50 },
    style: {background: "#fff",width: 200, fontSize: "20px",fontFamily: "Helvetica", boxShadow: "5px 5px 5px 0px rgba(0,0,0,.10)"}
  },
  
  {
    id: 'horizontal-2',
    sourcePosition: 'right',
    type: 'InputNode',
    targetPosition: 'left',
    data: { label: 'Set SMTP' },
    position: { x: 300, y: 50 },
    style: {background: "#fff",width: 200, fontSize: "20px",fontFamily: "Helvetica", boxShadow: "5px 5px 5px 0px rgba(0,0,0,.10)"}
  },
  
  {
    id: 'horizontal-3',
    sourcePosition: 'right',
    type: 'InputNode',
    targetPosition: 'left',
    data: { label: 'Set SMTP' },
    position: { x: 650, y: 25 },
    style: {background: "#fff",width: 200, fontSize: "20px",fontFamily: "Helvetica", boxShadow: "5px 5px 5px 0px rgba(0,0,0,.10)"}
  },
  {
    id: 'horizontal-4',
    sourcePosition: 'right',
    type: 'InputNode',
    targetPosition: 'left',
    data: { label: 'Set SMTP' },
    position: { x: 650, y: 100 },
    style: {background: "#fff",width: 200, fontSize: "20px",fontFamily: "Helvetica", boxShadow: "5px 5px 5px 0px rgba(0,0,0,.10)"}
  },

  {
    id: 'horizontal-e1-3',
    source: 'horizontal-1',
    type: 'smoothstep',
    target: 'horizontal-2',
    animated: true,
  },
  
  {
    id: 'horizontal-e3-5',
    source: 'horizontal-2',
    type: 'smoothstep',
    target: 'horizontal-3',
    animated: true,
  },
  {
    id: 'horizontal-e3-6',
    source: 'horizontal-2',
    type: 'smoothstep',
    target: 'horizontal-4',
    animated: true,
  },
  
];


let id = 0;
const getId = () => `dndnode_${id++}`;

export default function App() {
  const reactFlowWrapper = useRef(null);
  const [reactFlowInstance, setReactFlowInstance] = useState(null);
  const [elements, setElements] = useState(initialElements);
  const onConnect = (params) => setElements((els) => addEdge(params, els));
  const onElementsRemove = (elementsToRemove) =>
    setElements((els) => removeElements(elementsToRemove, els));
  const onLoad = (_reactFlowInstance) =>
    setReactFlowInstance(_reactFlowInstance);
  const onDragOver = (event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = "move";
  };
  const onDrop = (event) => {
    event.preventDefault();
    const reactFlowBounds = reactFlowWrapper.current.getBoundingClientRect();
    const type = event.dataTransfer.getData("application/reactflow");
    const position = reactFlowInstance.project({
      x: event.clientX - reactFlowBounds.left,
      y: event.clientY - reactFlowBounds.top
    });
    const newNode = {
      id: getId(),
      type,
      position,
      data: { label: `${type} node` }
    };
    setElements((es) => es.concat(newNode));
  };
  return (
    <div className="dndflow">
      <ReactFlowProvider>
        <div
          className="reactflow-wrapper"
          style={{ height: "400px", width: "780px" }}
          ref={reactFlowWrapper}
        >
          <ReactFlow
            elements={elements}
            onConnect={onConnect}
            onElementsRemove={onElementsRemove}
            onLoad={onLoad}
            onDrop={onDrop}
            onDragOver={onDragOver}
            nodeTypes={nodeTypes}
          >
            <Controls />
          </ReactFlow>
        </div>
        
      </ReactFlowProvider>
    </div>
  );
}
