import React, { useState  } from 'react';



import ReactFlow, { removeElements, addEdge } from 'react-flow-renderer';

import './Centerflow.css';

const onLoad = (reactFlowInstance) => reactFlowInstance.fitView();

const onNodeMouseEnter = (event, node) => console.log('mouse enter:', node);
const onNodeMouseMove = (event, node) => console.log('mouse move:', node);
const onNodeMouseLeave = (event, node) => console.log('mouse leave:', node);
const onNodeContextMenu = (event, node) => {
  event.preventDefault();
  console.log('context menu:', node);
};



const initialElements = [
    {
      id: 'horizontal-1',
      sourcePosition: 'right',
      type: 'input',
      className: 'dark-node',
      data: { label: 'Open Browser' },
      position: { x: 0, y: 50 },
      style: {background: "#fff",width: 200, fontSize: "20px",fontFamily: "Helvetica", boxShadow: "5px 5px 5px 0px rgba(0,0,0,.10)"}
    },
    
    {
      id: 'horizontal-2',
      sourcePosition: 'right',
      targetPosition: 'left',
      data: { label: 'Set SMTP' },
      position: { x: 300, y: 50 },
      style: {background: "#fff",width: 200, fontSize: "20px",fontFamily: "Helvetica", boxShadow: "5px 5px 5px 0px rgba(0,0,0,.10)"}
    },
    
    {
      id: 'horizontal-3',
      sourcePosition: 'right',
      targetPosition: 'left',
      data: { label: 'Set SMTP' },
      position: { x: 650, y: 25 },
      style: {background: "#fff",width: 200, fontSize: "20px",fontFamily: "Helvetica", boxShadow: "5px 5px 5px 0px rgba(0,0,0,.10)"}
    },
    {
      id: 'horizontal-4',
      sourcePosition: 'right',
      targetPosition: 'left',
      data: { label: 'Set SMTP' },
      position: { x: 650, y: 100 },
      style: {background: "#fff",width: 200, fontSize: "20px",fontFamily: "Helvetica", boxShadow: "5px 5px 5px 0px rgba(0,0,0,.10)"}
    },
  
    {
      id: 'horizontal-e1-3',
      source: 'horizontal-1',
      type: 'smoothstep',
      target: 'horizontal-2',
      animated: true,
    },
    
    {
      id: 'horizontal-e3-5',
      source: 'horizontal-2',
      type: 'smoothstep',
      target: 'horizontal-3',
      animated: true,
    },
    {
      id: 'horizontal-e3-6',
      source: 'horizontal-2',
      type: 'smoothstep',
      target: 'horizontal-4',
      animated: true,
    },
    
  ];
  
  const Centerflow = () => {
    const [elements, setElements] = useState(initialElements);
    const onElementsRemove = (elementsToRemove) =>
      setElements((els) => removeElements(elementsToRemove, els));
    const onConnect = (params) => setElements((els) => addEdge(params, els));
    
  
    return (
      <ReactFlow
        elements={elements}
        onElementsRemove={onElementsRemove}
        onConnect={onConnect}
        onLoad={onLoad}
        selectNodesOnDrag={false}
        onNodeMouseEnter={onNodeMouseEnter}
        onNodeMouseMove={onNodeMouseMove}
        onNodeMouseLeave={onNodeMouseLeave}
        onNodeContextMenu={onNodeContextMenu}
      >
       
        
      </ReactFlow>
    );
  };

export default Centerflow
